import React, { Component } from 'react';
import axios from 'axios'

class PostForm extends Component {
    constructor(props){
        super(props)
		this.state = {
			userId: '',
			title: '',
			body: '',
			posts: [],
			isLoading: false,
			error: null
		}
	}

	changeHandler = e => {
		this.setState({ [e.target.name]: e.target.value })
	}

	submitHandler = e => {
		e.preventDefault()
		this.setState({isLoading: true})
		console.log(this.state)
		axios
			.post('https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=5&key=AIzaSyBTgWzn1m8oOCYN0c4qDrjsktoqxmtvV6s&q=song', this.state)
			.then(response => response.json())
			.then(data => {
				this.setState({
							posts: response,
							isLoading: false
							})
				console.log(data)
			})
			.catch(error => {
				console.log(error)
			})
	}

	render() {
		const { userId, title, body, isLoading, error, posts } = this.state
		return (
			<div class="form-post">
				<form onSubmit={this.submitHandler}>
					<div>
						<input
							type="text"
							name="userId"
							value={userId}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="title"
							value={title}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="body"
							value={body}
							onChange={this.changeHandler}
						/>
					</div>
					<button type="submit">Submit</button>
				</form>

				<div>
					
				</div>
			</div>

		)
	}
}

export default PostForm
