import React, { Component } from 'react';
import axios from 'axios'

class PostForm extends Component {
    constructor(props){
        super(props)
		this.state = {
			userId: '',
			title: '',
			body: '',
			posts: [],
			isLoading: false,
			error: null
		}
	}

	changeHandler = e => {
		this.setState({ [e.target.name]: e.target.value })
	}

	submitHandler = e => {
		e.preventDefault()
		this.setState({isLoading: true})
		// console.log(this.state)
		axios
			.get('https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=5&key=AIzaSyBTgWzn1m8oOCYN0c4qDrjsktoqxmtvV6s&q=song')
			.then(response => {
				this.setState({
							posts: response.data.items,
							isLoading: false
							})
			})
			.catch(error => {
				console.log(error)
			})
	}

	render() {
		const { userId, title, body, isLoading, error, posts } = this.state
		return (
			<div class="form-post">
				<form onSubmit={this.submitHandler}>
					<div>
						<input
							type="text"
							name="userId"
							value={userId}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="title"
							value={title}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="body"
							value={body}
							onChange={this.changeHandler}
						/>
					</div>
					<button type="submit">Submit</button>
				</form>

				<div>
					{error ? <p>{error.message}</p> : null}
					{!isLoading ? (
						posts.map(post => {
							console.log(post.snippet.thumbnails)
						// const { username, name, email } = post;
						// return (
						// 	<div key={username}>
						// 	<p>Name: {name}</p>
						// 	<p>Email Address: {email}</p>
						// 	<hr />
						// 	</div>
						// );
						 })
					// If there is a delay in data, let's let the user know it's loading
					) : (
						<h3>Loading...</h3>
					)}
				</div>
			</div>

		)
	}
}

export default PostForm
